sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	var whatneeded = g_form.getValue('u_what_needed');

	//this next thing will clear the 'what needed' choice list
	g_form.clearOptions('u_what_needed');

	/* now if the value in 'Reqeust tyep' is hr,
	this will add 2 hr choices to the 'What derp' field */
	if(newValue == 'hr'){
		g_form.addOption('u_what_needed','hr1','Herrman Resurces 1');
		g_form.addOption('u_what_needed','hr2','Herrman Resurces 2');
		g_form.addOption('u_what_needed','other','Othdurr');
	}

	/* now if the value in 'Rederp terp' is facilities, this thing here will
	add two (2) facilities choisescs to the 'herpty derper' list. Here check it out: */
	if(newValue == 'facilities'){
		g_form.addOption('u_what_needed','facilities1','Facilities 1', 9);
		g_form.addOption('u_what_needed','facilities2','Facilities 2', 7);
		g_form.addOption('u_what_needed','other','Othdurrrrr');
	}

	// if the vaule in Request type is legal, then ur mother will be a whore forever sadly.
	if(newValue=='legal'){
		g_form.addOption('u_what_needed','legal1',"Your mom is a whore :/",0);
		g_form.addOption('u_what_needed','other','Nope');				
	}

   // also if teh form is still loading and it's not a new record, this will set the u_what_needed value to the value of your mom being a cumdumpster i mean the value from the record before it was loaded here check it out yoo
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed',whatneeded);
	}
}
